  error_log  /var/log/nginx/confrarianews-error.log warn;

  client_max_body_size 20m;

  location /api {
    include uwsgi_params;
    uwsgi_pass unix:/tmp/main/hackernews.sock;
  }

  location /openapi.json {
    include uwsgi_params;
    uwsgi_pass unix:/tmp/main/hackernews.sock;
  }

}

